SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, c.CourseName, c.CourseDescription, c.DegreeId, c.ECTS, d.Dept, d.DegreeDescription, d.TotalECTS, t.TeacherId, t.TeacherName, t.Address, t.BirthyearTeacher, t.Gender FROM CourseOffers co, Courses c, Degrees d, TeacherAssignmentsToCourses tac, Teachers t WHERE co.CourseOfferId = 1 AND co.CourseId = c.CourseId AND d.DegreeId = c.DegreeId AND co.CourseOfferId = tac.CourseOfferId AND tac.TeacherId = t.TeacherId;
SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, s.StudentId, s.StudentName, s.Address, s.BirthyearStudent, s.Gender, d.DegreeId, d.Dept, d.DegreeDescription, d.TotalECTS FROM StudentAssistants sa, StudentRegistrationsToDegrees srd, Students s, CourseOffers co, Degrees d WHERE sa.CourseOfferId = co.CourseOfferId AND sa.StudentRegistrationId = 3 AND sa.StudentRegistrationId = srd.StudentRegistrationId AND srd.StudentId = s.StudentId AND srd.DegreeId = d.DegreeId; 
SELECT AVG(Grade) FROM CourseRegistrations WHERE StudentRegistrationId = 3;

